import React from "react";
import "./App.css";
import Navbar from "./components/NavBar";
import Header from "./components/Header";
// import Card from "./components/Card";
import Main from "./components/Main";
import images from "./images.json";

class App extends React.Component {
  state = {
    images: images,
    score: 0,
    topScore: 0
    // cardArray: [],
  };

  shuffleCards = () => {
    return this.state.images.sort(() => Math.random() - 0.5);
  };

  handleCardClick(id, clicked) {
    let cardOrderArray = this.state.images;

    if (clicked) {
      cardOrderArray.forEach((image, i) => {
        cardOrderArray[i].clicked = false;
      });
      this.shuffleCards(this.state.images)
      return this.setState({
        score: 0,
        message: "Womp, try again."
      });
    } else {
      cardOrderArray.forEach((image, index) => {
        if (id === image.id) {
          cardOrderArray[index].clicked = true;
        }
        this.shuffleCards(this.state.cardData)

        const { highscore, score } = this.state;
        const newScore = score + 1;
        const newHighscore = newScore > highscore ? newScore : highscore;
        return this.setState({
          message: "You Guessed Correctly!",
          score: newScore,
          highscore: newHighscore,
        });
      });
    }
  }


  render() {
    return (
      <div>
        <Navbar score={this.state.score} topscore={this.state.topscore} />
        <Header />
        <Main handleCardClick={this.handleCardClick} />>
      </div>
    );
  }
}

export default App;
